CREATE OR ALTER PROCEDURE dbo.ClienteConVencimientoProximos
AS
BEGIN
    SELECT 
        C.Nombre,
        COUNT(*) AS CantidadFacturasConVencimientoProximo
    FROM Clientes C
    INNER JOIN Ventas V ON C.ID = V.[IDCliente ]
    INNER JOIN Facturas F ON V.ID = F.IDFactura
