GO
CREATE OR ALTER TRIGGER dbo.TR_ValidarCorreo
ON dbo.Proveedor_Tiene_Emails
INSTEAD OF INSERT
AS
BEGIN
    IF EXISTS (
        SELECT 1
        FROM INSERTED i
        WHERE i.Correo IS NULL 
