--
-- Create or alter trigger [TR_SumarStock] on table [dbo].[Items]
--
GO
PRINT (N'Create or alter trigger [TR_SumarStock] on table [dbo].[Items]')
GO
CREATE OR ALTER TRIGGER dbo.TR_SumarStock
ON dbo.Items
AFTER DELETE
AS
    UPDATE P
    SET P.Stock = P.Stock + D.Cantidad
    FROM Productos P
    INNER JOIN DELETED D ON P.ID = D.IDProducto
GO

